/*!

=========================================================
* Paper Kit React - v1.0.0
=========================================================

* Product Page: https://www.creative-tim.com/product/paper-kit-react

* Copyright 2019 Creative Tim (https://www.creative-tim.com)
* Licensed under MIT (https://github.com/creativetimofficial/paper-kit-react/blob/master/LICENSE.md)

* Coded by Creative Tim

=========================================================

* The above copyright notice and this permission notice shall be included in all copies or substantial portions of the Software.

*/
import React from "react";

import { render } from 'react-dom';
import { Stage, Layer, Star,Line,Circle, Text } from 'react-konva';
// reactstrap components
import { Button, Form, Input, Container, Row, Col } from "reactstrap";

// core components
import ExamplesNavbar from "components/Navbars/ExamplesNavbar.js";

let window_height = 400;
let window_width = 800;

function analyse_streets(begx, begy, endx, endy, direction){
  var x,y,end_x,end_y,delta_x,delta_y,points, color
  x = 250
  y = 10
  end_x = 350
  end_y = 110
  delta_x = end_x - x
  delta_y = end_y - y
  points = []

  if (direction){
    points = [0,0,delta_x, delta_y]
    color = "green"
  } else {
    points = [!(delta_x)*5, !(delta_y)*5,delta_x+!(delta_x)*5,delta_y+!(delta_y)*5]
    if (delta_x != 0 && delta_y != 0){
      points = [0,5,delta_x-5,delta_y]
    }
    color = "yellow"
  }
  return (
    <Line 
      x = {x}
      y = {y}
      points={points}
      stroke = {color}
      strokeWidth = {4}
    />
  )
}

function RegisterPage() {
  document.documentElement.classList.remove("nav-open");
  React.useEffect(() => {
    document.body.classList.add("register-page");
    return function cleanup() {
      document.body.classList.remove("register-page");
    };
  });
  return (
    <>
      <ExamplesNavbar />
      <div
        className="page-header"
        data-parallax={true}
        style={{
          backgroundColor:'rgba(0,0,0,.76)',
        }}
      >
        <div className="" />
        <Container>
          <Row style={{alignContent:'center',justifyContent:'center',border:10,borderColor:'white'}}>
            <Text style={{color:'white', fontWeight:'bold', fontSize:30}}>Map Analysis for: Espinho</Text>
            <Stage width={window_width} height={window_height}>
                <Layer id="map">
                  {analyse_streets(true)}
                  {analyse_streets(false)}

                {/* Aqui fazer as linhas (mudar isto para um for e dar draw automatico, mt cancer para ja)*/}
                {/* 1 linha */}
                <Line
                  x={250}
                  y={100}
                  points={[0, !(50), !(0)*100, 0]}
                  stroke="green"
                  strokeWidth="4"            
                />
                <Line
                  x={250}
                  y={100}
                  points={[0, 5, 100, 5]}
                  stroke="yellow"
                  strokeWidth="4"            
                />
                <Line
                  x={450}
                  y={100}
                  points={[0, 0, 100, 0]}
                  stroke="red"
                  strokeWidth="4"            
                />
                  {/* 2 linha */}
                <Line
                  x={250}
                  y={200}
                  points={[0, 0, 100, 0]}
                  stroke="red"
                  strokeWidth="4"            
                />
                <Line
                  x={350}
                  y={200}
                  points={[0, 0, 100, 0]}
                  stroke="red"
                  strokeWidth="4"            
                />
                <Line
                  x={450}
                  y={200}
                  points={[0, 0, 100, 0]}
                  stroke="green"
                  strokeWidth="4"            
                />
                {/* 3 linha */}

                <Line
                  x={250}
                  y={300}
                  points={[0, 0, 100, 0]}
                  stroke="green"
                  strokeWidth="4"            
                />
                <Line
                  x={350}
                  y={300}
                  points={[0, 0, 100, 0]}
                  stroke="green"
                  strokeWidth="4"            
                />
                <Line
                  x={450}
                  y={300}
                  points={[0, 0, 100, 0]}
                  stroke="red"
                  strokeWidth="4"            
                />

                {/* Agora fazer as colunas */}
                {/* 1 coluna */}
                <Line
                  x={250}
                  y={100}
                  points={[0, 0, 0, 100]}
                  stroke="red"
                  strokeWidth="4"            
                />
                <Line
                  x={250}
                  y={200}
                  points={[0, 0, 0, 100]}
                  stroke="green"
                  strokeWidth="4"            
                />

                {/* 2 coluna */}
                <Line
                  x={350}
                  y={100}
                  points={[0, 0, 0, 100]}
                  stroke="red"
                  strokeWidth="4"            
                />
                <Line
                  x={350}
                  y={200}
                  points={[0, 0, 0, 100]}
                  stroke="green"
                  strokeWidth="4"            
                />

                {/* 3 coluna */}
                <Line
                  x={450}
                  y={100}
                  points={[0, 0, 0, 100]}
                  stroke="green"
                  strokeWidth="4"            
                />
                <Line
                  x={450}
                  y={200}
                  points={[0, 0, 0, 100]}
                  stroke="yellow"
                  strokeWidth="4"            
                />

                {/* 4 coluna */}
                <Line
                  x={550}
                  y={100}
                  points={[0, 0, 0, 100]}
                  stroke="red"
                  strokeWidth="4"            
                />
                <Line
                  x={550}
                  y={200}
                  points={[0, 0, 0, 100]}
                  stroke="green"
                  strokeWidth="4"            
                />

                {/* Diagonais */}
                <Line
                  x={250}
                  y={100}
                  points={[0, 0, 100, 100]}
                  stroke="green"
                  strokeWidth="4"            
                />
                <Line
                  x={250}
                  y={100}
                  points={[5, 0, 100, 95]}
                  stroke="yellow"
                  strokeWidth="4"            
                />
                <Line
                  x={450}
                  y={200}
                  points={[0, 0, 120, -120]}
                  stroke="red"
                  strokeWidth="4"            
                />

                </Layer>
            </Stage>
          </Row>
        </Container>

      </div>
    </>
  );
}

export default RegisterPage;
